import React,{useState} from "react";
import "./App.css";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [todoEditing, setTodoEditing] = useState(null);
  // adding a todo to the list with the submit button
  function handleSubmit(e) {
    e.preventDefault();

    let todo = document.getElementById('todoAdd').value
    const newTodo = {
      id: new Date().getTime(),
      text: todo.trim(),
      completed: false,
    };
    if (newTodo.text.length > 0 ) {
        setTodos([...todos].concat(newTodo));
    } else {

        alert("Enter Valid Task");
    }
    document.getElementById('todoAdd').value = ""
  }

  //deleting a todo from the list using the delete button
  function deleteTodo(id) {
    let updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  }


  //adding a checkbox and toggle function
  function toggleComplete(id)
  {
    let updatedTodos = [...todos].map((todos)=> {
      if (todos.id === id )
      {
        todos.completed = !todos.completed;
      }
      return todos;
    });
    setTodos(updatedTodos);
  }

  // Edit the task 
  function submitEdits(newtodo)
  {
    const updatedTodos = [...todos].map((todos)=>{
      if (todo.id == newtodo.id) {
        todo.text = document.getElementById(newtodo.id).value;
      }
      return todo;
    });
    
  }
  
    return (
      <div id="todo-list">
        <h1>Todo List</h1>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              id = 'todoAdd'
            />
            <button type="submit">Add Todo</button>
        </form>
        {todos.map((todo) => <div className="todo" key={todo.id}>
            <div className="todo-text">{todo.text}
            <input type="checkbox" id="completed" checked={todo.completed} onChange={() => toggleComplete(todo.id)}/>
            </div>


        <button onClick ={()=> deleteTodo(todo.id)}>Delete</button>

        </div>)}
        </div>
  );
};

export default App;
